package com.retailstore.discountservice.controller;import com.retailstore.discountservice.request.BillRequest;import com.retailstore.discountservice.service.DiscountService;import com.retailstore.discountservice.validator.CalculateBillRequestValidator;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("/discount")@Slf4j@ControllerAdvicepublic class DiscountController {    private final DiscountService discountService;    @Autowired    public DiscountController(DiscountService discountService) {        this.discountService = discountService;    }    @PostMapping("/api/calculate")    public Double calculateDiscount(@RequestBody BillRequest bill) {        CalculateBillRequestValidator.validateRequest(bill);        return discountService.calculateDiscount(bill);    }}