package com.retailstore.discountservice.exception;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.ExceptionHandler;import org.springframework.web.bind.annotation.RestControllerAdvice;@RestControllerAdvicepublic class CurrencyExceptionHandler {    @ExceptionHandler(value = CurrencyNotFoundException.class)    public ResponseEntity<String> handleCurrencyNotFoundException(CurrencyNotFoundException ex) {        return new ResponseEntity<>(ex.getMessage(), HttpStatus.BAD_REQUEST);    }    @ExceptionHandler(value = UnsupportedCurrencyException.class)    public ResponseEntity<String> handleUnsupportedCurrencyException(UnsupportedCurrencyException ex) {        return new ResponseEntity<>(ex.getMessage(), HttpStatus.BAD_REQUEST);    }}