package com.retailstore.discountservice.model.impl;import com.retailstore.discountservice.model.BaseDiscount;public class BillAmountBasedDiscount implements BaseDiscount {    private static final Double DISCOUNT_VALUE = 5.0;    private static final Double MINIMUM_BILL_AMOUNT = 100.0;    @Override    public Double applyDiscount(Double amount) {        return (amount / MINIMUM_BILL_AMOUNT) * DISCOUNT_VALUE;    }}