package com.retailstore.discountservice.model.impl;import com.retailstore.discountservice.constant.DiscountPercentage;import com.retailstore.discountservice.model.BaseDiscount;import lombok.AllArgsConstructor;@AllArgsConstructorpublic class PercentageBasedDiscount implements BaseDiscount {    private DiscountPercentage discountPercentage;    @Override    public Double applyDiscount(Double amount) {        return (amount * discountPercentage.getDiscount());    }}